example/animation.html.body:
c-animation

动画组件
示例



    Transform












    Others











    Anim




const createAnimation = cml.createAnimation();

class CAnimation {
  data = {
    animationData: createAnimation(),
    current_rotate: 0,
    current_scale: 1,
    current_translate: '50px',
    current_transform: [45, 1],
    current_color: '#FF0000',
    current_opacity: 1
  }
  methods = {
    rotate () {
      let animationCreation = createAnimation()
      while (this.current_rotate !== 360) {
        this.current_rotate += 90;
        animationCreation = animationCreation
          .rotate(this.current_rotate)
          .step({
            duration: 500,
            timingFunction: 'ease-in-out',
            delay: 0
          })
      }
      this.animationData = animationCreation.export();
      this.current_rotate = 0
    },
    scale () {
      this.current_scale = this.current_scale === 2 ? .5 : 2
      this.animationData = createAnimation()
        .scale(this.current_scale)
        .step({
          duration: 500,
          timingFunction: 'linear',
          delay: 0
        })
        .export()
    },
    translate () {
      this.current_translate = this.current_translate === '50px'? '-50px' : '50px';
      this.animationData = createAnimation()
        .translate(this.current_translate, this.current_translate)
        .step({
          duration: 500,
          timingFunction: 'ease-in',
          delay: 0
        })
        .export()
    },
    transform () {
      this.current_transform = this.current_transform[0] === 0 ?  [45, 1.2]:[0, 1];
      this.animationData = createAnimation()
        .rotate(this.current_transform[0])
        .scale(this.current_transform[1])
        .step({
          duration: 500,
          timingFunction: 'ease-out',
          delay: 0
        })
        .rotate("-90deg")
        .scale(1.2)
        .step({
          duration: 500,
          timingFunction: 'ease-out',
          delay: 0
        })
        .export()
    },
    backgroundColor () {
      this.current_color = this.current_color === '#F0AD4E' ? '#D9534F' : '#F0AD4E';
      this.animationData = createAnimation()
        .backgroundColor(this.current_color)
        .step({
          duration: 500,
          timingFunction: 'linear',
          delay: 0
        })
        .export()
    },
    opacity () {
      this.current_opacity = this.current_opacity === 1 ? 0.1 : 1;
      this.animationData = createAnimation()
        .opacity(this.current_opacity)
        .step({
          duration: 500,
          timingFunction: 'linear',
          delay: 0
        })
        .export()
    },
    composite () {
      this.current_transform = this.current_transform[0] === 0 ?  [45, 1.5]:[0, 1];
      this.current_color = this.current_color === '#F0AD4E' ? '#D9534F' : '#F0AD4E';
      this.current_opacity = this.current_opacity === 1 ? 0.1 : 1;
      this.current_translate = this.current_translate === '50px'? '-50px' : '50px';
      this.animationData = createAnimation()
        .rotate(this.current_transform[0])
        .scale(this.current_transform[1])
        .opacity(this.current_opacity)
        .backgroundColor(this.current_color)
        .translate(this.current_translate, this.current_translate)
        .step({
          duration: 1000,
          timingFunction: 'ease-out',
          delay: 0
        })
        .export()
    }
  }
}

export default new CAnimation();



