c-refresh

上拉&下拉刷新
示例









              {{item.label}}


      = sumPage }}">没有更多了...

        上拉刷新...






import cml from '@didi/chameleon-api'
class Refresh {
  data = {
    topRefreshing:false,
    bottomRefreshing: false,
    bottomOffset: 20,
    scrollDirection: 'vertical',
    panels: [
    ],
    rows: [],
    sumPage: 4,
    page: 0,
    loadingTextStyle: ''
  }

  methods = {
    getPanels (direction) {
      if (this.page >= this.sumPage) return
      let rows = this.randomfn()
      this.panels = direction == 1 ? [...this.panels, ...rows]: [...rows, ...this.panels];
      this.page++;
    },
    onrefreshUp (e) {
      this.topRefreshing = e.detail.value
      setTimeout(() => {
        this.getPanels(-1)
        this.topRefreshing = false
      }, 2000)
    },
    onrefreshDown (e) {
      this.bottomRefreshing = e.detail.value
      setTimeout(() => {
        this.getPanels(1)
        this.bottomRefreshing = false
      }, 2000)
    },
    change (e) {
        let target = e.currentTarget
        let dataset = target.dataset
        let i = dataset.idx

        const item = this.panels[i]
        if (item) {
            item.height = item.height === 200 ? 400 : 200
            item.width = item.width === 330 ? 730 : 330
            item.computedStyle = this.$cmlStyle(`height:${item.height}px;width:${item.width}px;background-color:${item.bgc};opacity:${item.opacity}`)
        }
    },
    randomfn () {
        let ary = [];
        for(let i = 1; i= this.sumPage) return
      this.bottomRefreshing = true;
    }
  }

  created(res) {
    this.getPanels()
    this.loadingTextStyle = this.$cmlStyle('color:#666;font-size:18px;margin:30px auto;')
  }

}

export default new Refresh();


